          .selectedItemHeight(15) // 设置选中导航点高度
          .color(Color.Gray) // 设置导航点颜色
          .selectedColor(Color.Blue)) // 设置选中导航点颜色
      .displayArrow({ 
        showBackground: true, // 显示箭头背景
        isSidebarMiddle: true, // 箭头位于侧边栏中间
        backgroundSize: 24, // 箭头背景大小
