        console.info("targetIndex: " + targetIndex) // 打印目标索引
        console.info("current offset: " + extraInfo.currentOffset) // 打印当前偏移量
        console.info("target offset: " + extraInfo.targetOffset) // 打印目标偏移量
        console.info("velocity: " + extraInfo.velocity) // 打印速度
      })
      .onAnimationEnd((index: number, extraInfo: SwiperAnimationEvent) => {
        console.info("index: " + index) // 打印当前索引
        console.info("current offset: " + extraInfo.currentOffset) // 打印当前偏移量
