@Entry
@Component
struct TabsCustomAnimationExample {
  @State data: itemType[] = [
    {
      text: 'Red',
      backgroundColor: Color.Red
    },
    {
      text: 'Yellow',
      backgroundColor: Color.Yellow
    },
    {
      text: 'Blue',
      backgroundColor: Color.Blue
    }
  ]
  @State opacityList: number[] = [] // 存储每个Tab页面的透明度值
  @State scaleList: number[] = [] // 存储每个Tab页面的缩放值

  private durationList: number[] = [] // 存储每个Tab页面的动画持续时间
  private timeoutList: number[] = [] // 存储每个Tab页面的动画延迟时间

  // 定义自定义的TabContentAnimatedTransition
  private customContentTransition: (from: number, to: number) => TabContentAnimatedTransition = (from: number, to: number) => {
    let tabContentAnimatedTransition = {
      timeout: this.timeoutList[from], // 设置动画延迟时间
      transition: (proxy: TabContentTransitionProxy) => {
        this.scaleList[from] = 1.0 // 设置起始Tab页面的初始缩放值
        this.scaleList[to] = 0.5 // 设置目标Tab页面的初始缩放值
        this.opacityList[from] = 1.0 // 设置起始Tab页面的初始透明度值
        this.opacityList[to] = 0.5 // 设置目标Tab页面的初始透明度值
        animateTo({
          duration: this.durationList[from], // 设置动画持续时间
          onFinish: () => {
            proxy.finishTransition() // 动画完成后调用finishTransition方法
          }
        }, () => {
          this.scaleList[from] = 0.5 // 设置起始Tab页面的最终缩放值
          this.scaleList[to] = 1.0 // 设置目标Tab页面的最终缩放值
          this.opacityList[from] = 0.5 // 设置起始Tab页面的最终透明度值
          this.opacityList[to] = 1.0 // 设置目标Tab页面的最终透明度值
        })
      }
    } as TabContentAnimatedTransition
    return tabContentAnimatedTransition
  }

  // 组件初始化时设置动画参数
  aboutToAppear(): void {
    let duration = 1000
    let timeout = 1000
    for (let i = 1; i <= this.data.length; i++) {
      this.opacityList.push(1.0) // 初始化透明度值
      this.scaleList.push(1.0) // 初始化缩放值
      this.durationList.push(duration * i) // 设置动画持续时间
      this.timeoutList.push(timeout * i) // 设置动画延迟时间
    }
  }

  build() {
    Column() {
      Tabs() {
        ForEach(this.data, (item: itemType, index: number) => {
          TabContent() {}
            .tabBar(item.text) // 设置Tab页面的文本标签
            .backgroundColor(item.backgroundColor) // 设置Tab页面的背景颜色
            .opacity(this.opacityList[index]) // 设置Tab页面的透明度
            .scale({ x: this.scaleList[index], y: this.scaleList[index] }) // 设置Tab页面的缩放
        })
      }
      .backgroundColor(0xf1f3f5) // 设置Tabs组件的背景颜色
      .width('100%') // 设置Tabs组件的宽度
      .height(500) // 设置Tabs组件的高度
      .customContentTransition(this.customContentTransition) // 设置自定义的TabContentAnimatedTransition
    }
  }
}

