import { ComponentContent, UIContext } from "@kit.ArkUI"

// 定义一个Params类，用于传递文本内容
class Params {
  text: string = ""

  constructor(text: string) {
    this.text = text;
  }
}

// 使用Builder装饰器定义一个构建文本组件的函数
@Builder
function buildText(params: Params) {
  Column() {
    Text(params.text)
      .fontSize(20) // 设置文本字体大小
      .fontWeight(FontWeight.Bold) // 设置文本字体粗细
      .margin(20) // 设置文本外边距
  }
}

// 主组件Index，使用@Entry和@Component装饰器
@Entry
@Component
struct Index {
  @State message1: string = "tabBar1" // 定义状态变量message1，初始值为"tabBar1"
  @State message2: string = "tabBar2" // 定义状态变量message2，初始值为"tabBar2"
  context: UIContext = this.getUIContext() // 获取UI上下文
  private count1 = 0; // 定义私有变量count1，用于记录更新次数
  private count2 = 0; // 定义私有变量count2，用于记录更新次数
  private controller: TabsController = new TabsController(); // 创建Tabs控制器
  tabBar1: ComponentContent<Params> = new ComponentContent<Params>(this.context, wrapBuilder<[Params]>(buildText), new Params(this.message1)); // 创建ComponentContent实例tabBar1
  tabBar2: ComponentContent<Params> = new ComponentContent<Params>(this.context, wrapBuilder<[Params]>(buildText), new Params(this.message2)); // 创建ComponentContent实例tabBar2

  build() {
    Row() {
