import { WaterFlowDataSource } from './WaterFlowDataSource'

// 定义入口组件WaterFlowDemo
@Entry
@Component
struct WaterFlowDemo {
  // 定义状态变量minSize，表示子项的最小尺寸
  @State minSize: number = 80
  // 定义状态变量maxSize，表示子项的最大尺寸
  @State maxSize: number = 180
  // 定义状态变量colors，表示子项的背景颜色数组
  @State colors: number[] = [0xFFC0CB, 0xDA70D6, 0x6B8E23, 0x6A5ACD, 0x00FFFF, 0x00FF7F]
  // 定义数据源对象
  dataSource: WaterFlowDataSource = new WaterFlowDataSource()
  // 定义私有变量itemWidthArray，用于存储子项的宽度
  private itemWidthArray: number[] = []
  // 定义私有变量itemHeightArray，用于存储子项的高度
  private itemHeightArray: number[] = []

  // 定义获取随机尺寸的方法
  getSize() {
    // 生成一个介于0和maxSize之间的随机数
    let ret = Math.floor(Math.random() * this.maxSize)
    // 如果随机数小于minSize，则返回minSize，否则返回随机数
    return (ret > this.minSize ? ret : this.minSize)
  }

  // 定义设置子项尺寸数组的方法
  setItemSizeArray() {
    // 循环100次，生成100个子项的宽度和高度
    for (let i = 0; i < 100; i++) {
      this.itemWidthArray.push(this.getSize())
      this.itemHeightArray.push(this.getSize())
    }
  }

  // 组件即将显示时调用的方法
  aboutToAppear() {
    // 调用设置子项尺寸数组的方法
    this.setItemSizeArray()
  }

  // 构建组件的方法
  build() {
    // 使用Column布局，设置子项之间的间距为2
    Column({ space: 2 }) {
      // 使用WaterFlow布局
      WaterFlow() {
        // 使用LazyForEach遍历数据源，生成子项
        LazyForEach(this.dataSource, (item: number) => {
          // 使用FlowItem组件
          FlowItem() {
            // 使用Column布局
            Column() {
              // 显示文本，内容为"N"加上数据源中的item值
              Text("N" + item).fontSize(12).height('16')
              // 显示图片，图片路径为res/waterFlowTest(item % 5).jpg
              Image('res/waterFlowTest(' + item % 5 + ').jpg')
            }
          }
          // 设置子项的宽度为100%
          .width('100%')
          // 设置子项的高度为itemHeightArray中对应索引的值
          .height(this.itemHeightArray[item % 100])
          // 设置子项的背景颜色为colors数组中对应索引的值
          .backgroundColor(this.colors[item % 5])
        }, (item: string) => item) // 设置key值为item
      }
      // 设置WaterFlow的列模板为自动填充，每列宽度为80
      .columnsTemplate('repeat(auto-fill,80)')
      // 设置列之间的间距为10
      .columnsGap(10)
      // 设置行之间的间距为5
      .rowsGap(5)
      // 设置内边距，左边距为5
      .padding({left:5})
      // 设置背景颜色为0xFAEEE0
      .backgroundColor(0xFAEEE0)
      // 设置宽度为100%
      .width('100%')
      // 设置高度为100%
      .height('100%')
    }
  }
}

