// 定义入口组件，实现瀑布流布局
@Entry
@Component
struct WaterFlowDemo {
  minSize: number = 80 // 最小尺寸
  maxSize: number = 180 // 最大尺寸
  colors: number[] = [0xFFC0CB, 0xDA70D6, 0x6B8E23, 0x6A5ACD, 0x00FFFF, 0x00FF7F] // 颜色数组
  @State columns: number = 2 // 定义状态变量，用于存储当前列数
  dataSource: WaterFlowDataSource = new WaterFlowDataSource() // 数据源实例
  private itemWidthArray: number[] = [] // 存储每个项的宽度
  private itemHeightArray: number[] = [] // 存储每个项的高度

  // 获取随机尺寸
  getSize() {
    let ret = Math.floor(Math.random() * this.maxSize) // 生成随机尺寸
    return (ret > this.minSize ? ret : this.minSize) // 返回随机尺寸，确保不小于最小尺寸
  }

  // 设置项的尺寸数组
  setItemSizeArray() {
    for (let i = 0; i < 100; i++) {
      this.itemWidthArray.push(this.getSize()) // 添加随机宽度
      this.itemHeightArray.push(this.getSize()) // 添加随机高度
    }
  }

  // 组件首次出现时调用的方法
  aboutToAppear() {
    let lastCount = AppStorage.get<number>('columnsCount') // 获取上一次保存的列数
    if (typeof lastCount != 'undefined') {
      this.columns = lastCount // 恢复上一次的列数
    }
    this.setItemSizeArray() // 初始化项的尺寸数组
  }

  // 构建组件UI
  build() {
    Column({ space: 2 }) {
      Row() {
        Text('双指缩放改变列数') // 提示文本
          .height('5%') // 文本高度
          .margin({ top: 10, left: 20 }) // 文本外边距
      }

      WaterFlow() {
        LazyForEach(this.dataSource, (item: number) => { // 遍历数据源
          FlowItem() {
            ReusableFlowItem({ item: item }) // 创建可复用的瀑布流项
          }
          .width('100%') // 项的宽度
          .height(this.itemHeightArray[item % 100]) // 项的高度
          .backgroundColor(this.colors[item % 5]) // 项的背景颜色
        }, (item: string) => item) // 唯一标识符
      }
      .columnsTemplate('1fr '.repeat(this.columns)) // 列模板
      .columnsGap(10) // 列间距
      .rowsGap(5) // 行间距
      .backgroundColor(0xFAEEE0) // 背景颜色
      .width('100%') // 宽度
      .height('100%') // 高度
      .layoutWeight(1) // 布局权重

      .animation({ // 动画配置
        duration: 300, // 动画时长
        curve: Curve.Smooth // 动画曲线
      })
      .priorityGesture( // 优先手势
        PinchGesture() // 双指缩放手势
          .onActionEnd((event: GestureEvent) => { // 手势结束时的回调
            console.info('end scale:' + event.scale) // 打印缩放比例

            if (event.scale > 2) { // 缩放比例大于2时
              this.columns-- // 减少列数
            } else if (event.scale < 0.6) { // 缩放比例小于0.6时
              this.columns++ // 增加列数
            }

            this.columns = Math.min(4, Math.max(1, this.columns)); // 限制列数范围
            AppStorage.setOrCreate<number>('columnsCount', this.columns) // 保存当前列数
          })
      )
    }
  }
}

examples.description:
实现一个瀑布流布局组件，支持动态加载数据、滚动事件监听、以及数据源的管理。组件包含一个WaterFlow布局，内部使用LazyForEach组件动态加载数据项，并根据数据源的变化动态调整布局。
定义一个数据源类，实现IDataSource接口，用于管理数据和通知数据变化
export class WaterFlowDataSource implements IDataSource {
private dataArray: number[] = []  存储数据的数组
private listeners: DataChangeListener[] = []  存储数据变化监听器的数组
构造函数，初始化数据数组，填充100个数据项
constructor() {
for (let i = 0; i < 100; i++) {
this.dataArray.push(i)
}
}
获取指定索引的数据项
public getData(index: number): number {
return this.dataArray[index]
}
通知所有监听器数据已重新加载
notifyDataReload(): void {
this.listeners.forEach(listener => {
listener.onDataReloaded()
})
}
通知所有监听器在指定索引处添加了数据
notifyDataAdd(index: number): void {
this.listeners.forEach(listener => {
listener.onDataAdd(index)
})
}
通知所有监听器在指定索引处的数据已更改
notifyDataChange(index: number): void {
this.listeners.forEach(listener => {
listener.onDataChange(index)
})
}
通知所有监听器在指定索引处的数据已删除
notifyDataDelete(index: number): void {
this.listeners.forEach(listener => {
listener.onDataDelete(index)
})
}
通知所有监听器数据已移动
notifyDataMove(from: number, to: number): void {
this.listeners.forEach(listener => {
listener.onDataMove(from, to)
})
}
通知所有监听器数据集已更改
notifyDatasetChange(operations: DataOperation[]): void {
this.listeners.forEach(listener => {
listener.onDatasetChange(operations);
})
}
获取数据总数
public totalCount(): number {
return this.dataArray.length
}
注册数据变化监听器
registerDataChangeListener(listener: DataChangeListener): void {
if (this.listeners.indexOf(listener) < 0) {
this.listeners.push(listener)
}
}
注销数据变化监听器
unregisterDataChangeListener(listener: DataChangeListener): void {
const pos = this.listeners.indexOf(listener)
if (pos >= 0) {
this.listeners.splice(pos, 1)
}
}
在数据数组的开头添加一个数据项
public add1stItem(): void {
this.dataArray.splice(0, 0, this.dataArray.length)
this.notifyDataAdd(0)
}
在数据数组的末尾添加一个数据项
public addLastItem(): void {
this.dataArray.splice(this.dataArray.length, 0, this.dataArray.length)
this.notifyDataAdd(this.dataArray.length - 1)
}
在指定索引处添加一个数据项
public addItem(index: number): void {
this.dataArray.splice(index, 0, this.dataArray.length)
this.notifyDataAdd(index)
}
删除数据数组的第一个数据项
public delete1stItem(): void {
this.dataArray.splice(0, 1)
this.notifyDataDelete(0)
}
删除数据数组的第二个数据项
public delete2ndItem(): void {
this.dataArray.splice(1, 1)
this.notifyDataDelete(1)
}
删除数据数组的最后一个数据项
public deleteLastItem(): void {
this.dataArray.splice(-1, 1)
this.notifyDataDelete(this.dataArray.length)
}
删除指定索引处的数据项
public deleteItem(index: number): void {
this.dataArray.splice(index, 1)
this.notifyDataDelete(index)
}
重新加载数据，删除指定索引处的数据项
public reload(): void {
this.dataArray.splice(1, 1)
this.dataArray.splice(3, 2)
this.notifyDataReload()
}
}
