// 定义一个入口组件WaterFlowDemo，用于展示瀑布流布局
@Entry
@Component
struct WaterFlowDemo {
  minSize: number = 80 // 定义项目高度的最小值
  maxSize: number = 180 // 定义项目高度的最大值
  fontSize: number = 24 // 定义字体大小
  colors: number[] = [0xFFC0CB, 0xDA70D6, 0x6B8E23, 0x6A5ACD, 0x00FFFF, 0x00FF7F] // 定义颜色数组
  scroller: Scroller = new Scroller() // 创建一个滚动控制器
  dataSource: WaterFlowDataSource = new WaterFlowDataSource() // 创建一个数据源实例
  dataCount: number = this.dataSource.totalCount() // 获取数据源的总项目数
  private itemHeightArray: number[] = [] // 定义一个数组，用于存储每个项目的高度
  @State sections: WaterFlowSections = new WaterFlowSections() // 定义一个状态变量sections，用于存储瀑布流的节
  sectionMargin: Margin = { top: 10, left: 5, bottom: 10, right: 5 } // 定义节的边距

  // 定义一个单列节的配置
  oneColumnSection: SectionOptions = {
    itemsCount: 4, // 节中的项目数
    crossCount: 1, // 节中的列数
    columnsGap: '5vp', // 列之间的间距
    rowsGap: 10, // 行之间的间距
    margin: this.sectionMargin, // 节的边距
    onGetItemMainSizeByIndex: (index: number) => { // 根据索引获取项目的主尺寸（高度）
      return this.itemHeightArray[index % 100]
    }
  }

  // 定义一个双列节的配置
  twoColumnSection: SectionOptions = {
    itemsCount: 2, // 节中的项目数
    crossCount: 2, // 节中的列数
    onGetItemMainSizeByIndex: (index: number) => { // 根据索引获取项目的主尺寸（高度）
      return 100
    }
  }

  // 定义最后一个节的配置
  lastSection: SectionOptions = {
    itemsCount: 20, // 节中的项目数
    crossCount: 2, // 节中的列数
    onGetItemMainSizeByIndex: (index: number) => { // 根据索引获取项目的主尺寸（高度）
      return this.itemHeightArray[index % 100]
    }
  }

  // 获取随机尺寸的方法
  getSize() {
    let ret = Math.floor(Math.random() * this.maxSize)
    return (ret > this.minSize ? ret : this.minSize)
  }

  // 设置项目高度数组的方法
  setItemSizeArray() {
    for (let i = 0; i < 100; i++) {
      this.itemHeightArray.push(this.getSize())
    }
  }

  // 当组件首次出现时调用，初始化项目高度数组并设置节的配置
  aboutToAppear() {
    this.setItemSizeArray()

    let sectionOptions: SectionOptions[] = []
    let count = 0
    let oneOrTwo = 0
    while (count < this.dataCount) {
      if (this.dataCount - count < 20) {
        this.lastSection.itemsCount = this.dataCount - count
        sectionOptions.push(this.lastSection)
        break;
      }
      if (oneOrTwo++ % 2 == 0) {
        sectionOptions.push(this.oneColumnSection)
        count += this.oneColumnSection.itemsCount
      } else {
        sectionOptions.push(this.twoColumnSection)
        count += this.twoColumnSection.itemsCount
      }
    }
    this.sections.splice(0, 0, sectionOptions)
  }

  // 构建组件的UI结构
  build() {
    Column({ space: 2 }) {
      Row() {
        Button('splice')
          .height('5%')
          .onClick(() => {
            let totalCount: number = this.dataSource.totalCount()
            let newSection: SectionOptions = {
              itemsCount: totalCount,
              crossCount: 2,
              onGetItemMainSizeByIndex: (index: number) => {
                return this.itemHeightArray[index % 100]
              }
            }
            let oldLength: number = this.sections.length()
            this.sections.splice(0, oldLength, [newSection])
          })
          .margin({ top: 10, left: 20 })
        Button('update')
          .height('5%')
          .onClick(() => {
            let newSection: SectionOptions = {
              itemsCount: 6,
              crossCount: 3,
              columnsGap: 5,
              rowsGap: 10,
              margin: this.sectionMargin,
              onGetItemMainSizeByIndex: (index: number) => {
                return this.itemHeightArray[index % 100]
              }
            }
            this.dataSource.addItem(this.oneColumnSection.itemsCount)
            this.dataSource.addItem(this.oneColumnSection.itemsCount + 1)
            this.dataSource.addItem(this.oneColumnSection.itemsCount + 2)
            this.dataSource.addItem(this.oneColumnSection.itemsCount + 3)
            const result: boolean = this.sections.update(1, newSection)
            console.info('update:' + result)
          })
          .margin({ top: 10, left: 20 })
        Button('delete')
          .height('5%')
          .onClick(() => {
            let newSection: SectionOptions = {
              itemsCount: 2,
              crossCount: 2,
              columnsGap: 5,
              rowsGap: 10,
              margin: this.sectionMargin,
              onGetItemMainSizeByIndex: (index: number) => {
                return this.itemHeightArray[index % 100]
              }
            }
            this.dataSource.deleteItem(this.oneColumnSection.itemsCount)
            this.dataSource.deleteItem(this.oneColumnSection.itemsCount)
            this.dataSource.deleteItem(this.oneColumnSection.itemsCount)
            this.dataSource.deleteItem(this.oneColumnSection.itemsCount)
            this.sections.update(1, newSection)
          })
          .margin({ top: 10, left: 20 })
        Button('values')
          .height('5%')
          .onClick(() => {
            const sections: Array<SectionOptions> = this.sections.values();
            for (const value of sections) {
              console.log(JSON.stringify(value));
            }
            console.info('count:' + this.sections.length())
          })
          .margin({ top: 10, left: 20 })
      }.margin({ bottom: 20 })

      WaterFlow({ scroller: this.scroller, sections: this.sections }) {
        LazyForEach(this.dataSource, (item: number) => {
          FlowItem() {
            ReusableFlowItem({ item: item })
          }
          .width('100%')
          .backgroundColor(this.colors[item % 5])
        }, (item: string) => item)
      }
      .columnsTemplate('1fr 1fr') 
      .columnsGap(10)
      .rowsGap(5)
      .backgroundColor(0xFAEEE0)
      .width('100%')
      .height('100%')
      .layoutWeight(1)
      .onScrollIndex((first: number, last: number) => {
        if (last + 20 >= this.dataSource.totalCount()) {
          for (let i = 0; i < 100; i++) {
            this.dataSource.addLastItem()
          }
          const sections: Array<SectionOptions> = this.sections.values();
          let newSection: SectionOptions = sections[this.sections.length() - 1];
          newSection.itemsCount += 100;
          this.sections.update(-1, newSection);
        }
      })
    }
  }
}

