@Entry
@Component
struct AlertDialogExample {
  build() {
    Column({ space: 5 }) {
      Button('AlertDialog Set Duration')
        .onClick(() => {
          // 显示AlertDialog，并设置自定义动画效果和持续时间
          AlertDialog.show({
            title: 'AlertDialog 1', // 对话框标题
            message: 'Set Animation Duration open 3 second, close 100ms', // 对话框消息内容
            autoCancel: True, // 自动取消对话框
            alignment: DialogAlignment.Top, // 对话框对齐方式为顶部
            offset: { dx: 0, dy: -20 }, // 对话框偏移量
            gridCount: 3, // 对话框网格数
            transition: TransitionEffect.asymmetric(
              // 显示动画效果：透明度和缩放，持续3秒，曲线为Sharp
              TransitionEffect.OPACITY.animation({ duration: 3000, curve: Curve.Sharp }).combine(
                TransitionEffect.scale({ x: 1.5, y: 1.5 }).animation({ duration: 3000, curve: Curve.Sharp })
              ),
              // 退出动画效果：透明度和缩放，持续100毫秒，曲线为Smooth
              TransitionEffect.OPACITY.animation({ duration: 100, curve: Curve.Smooth }).combine(
                TransitionEffect.scale({ x: 0.5, y: 0.5 }).animation({ duration: 100, curve: Curve.Smooth })
              )
            ),
            confirm: {
              value: 'button', // 确认按钮文本
              action: () => {
                console.info('Button-clicking callback'); // 确认按钮点击回调
              }
            },
            cancel: () => {
              console.info('Closed callbacks'); // 对话框关闭回调
            }
          });
        })
        .backgroundColor(0x317aff) // 按钮背景颜色
        .height("88px") // 按钮高度
    }
    .width('100%') // 列宽度
    .margin({ top: 5 }) // 列顶部外边距
  }
}

