@Component
struct CustomDialogUser {
  @State textValue: string = '' // 文本输入框的初始值
  @State inputValue: string = 'click me' // 按钮的初始文本

  // 创建一个自定义对话框控制器，用于管理对话框的显示和隐藏
  dialogController: CustomDialogController | null = new CustomDialogController({
    builder: CustomDialogExample({
      cancel: ()=> { this.onCancel() }, // 取消按钮的回调函数
      confirm: ()=> { this.onAccept() }, // 确认按钮的回调函数
      textValue: $textValue, // 绑定文本输入框的值
      inputValue: $inputValue // 绑定按钮的文本
    }),
    cancel: this.exitApp, // 点击空白区域或按下返回键时的回调函数
    autoCancel: True, // 是否自动取消对话框
    onWillDismiss:(dismissDialogAction: DismissDialogAction)=> {
      console.info("reason=" + JSON.stringify(dismissDialogAction.reason)) // 对话框即将关闭时的回调函数，输出关闭原因
      console.log("dialog onWillDismiss")
      if (dismissDialogAction.reason == DismissReason.PRESS_BACK) {
        dismissDialogAction.dismiss() // 按下返回键时关闭对话框
      }
      if (dismissDialogAction.reason == DismissReason.TOUCH_OUTSIDE) {
        dismissDialogAction.dismiss() // 点击对话框外部时关闭对话框
      }
    },
    alignment: DialogAlignment.Bottom, // 对话框的对齐方式，底部对齐
    offset: { dx: 0, dy: -20 }, // 对话框的偏移量
    gridCount: 4, // 对话框的网格数
    customStyle: False, // 是否使用自定义样式
    cornerRadius: 10, // 对话框的圆角半径
  })

  // 在自定义组件即将析构销毁时将dialogController置空
  aboutToDisappear() {
    this.dialogController = null
  }

  // 取消按钮的回调函数
  onCancel() {
    console.info('Callback when the first button is clicked')
  }

  // 确认按钮的回调函数
  onAccept() {
    console.info('Callback when the second button is clicked')
  }

  // 点击空白区域的回调函数
  exitApp() {
    console.info('Click the callback in the blank area')
  }

  // 构建UI界面
  build() {
    Column() {
      Button(this.inputValue) // 创建一个按钮，按钮文本为inputValue
        .onClick(() => {
          if (this.dialogController != null) {
            this.dialogController.open() // 点击按钮时打开对话框
          }
        }).backgroundColor(0x317aff) // 设置按钮的背景颜色
    }.width('100%').margin({ top: 5 }) // 设置列的宽度为100%，顶部外边距为5
  }
}

