    backgroundColor: Color.White, // 设置对话框的背景颜色为白色
    shadow: ({ radius: 20, color: Color.Grey, offsetX: 50, offsetY: 0}) // 设置对话框的阴影效果
  })

  // 在自定义组件即将析构销毁时将dialogController置空
  aboutToDisappear() {
    this.dialogController = null // 将dialogController置空
  }

  // 取消按钮的回调函数
  onCancel() {
    console.info('Callback when the first button is clicked') // 打印取消回调日志
  }

  // 确认按钮的回调函数
  onAccept() {
    console.info('Callback when the second button is clicked') // 打印确认回调日志
  }

  // 点击空白区域的回调函数
  existApp() {
    console.info('Click the callback in the blank area') // 打印点击空白区域回调日志
  }

  // 构建UI布局
  build() {
    Column() {
      Button('click me') // 显示一个按钮，点击按钮时打开对话框
