@Entry
@Component
struct VideoCreateComponent {
  @State videoSrc: Resource = $rawfile('video1.mp4') // 视频源文件，初始为video1.mp4
  @State previewUri: Resource = $r('app.media.poster1') // 视频预览图，初始为poster1
  @State curRate: PlaybackSpeed = PlaybackSpeed.Speed_Forward_1_00_X // 当前播放速度，初始为1倍速
  @State isAutoPlay: boolean = False // 是否自动播放，初始为不自动播放
  @State showControls: boolean = True // 是否显示控制条，初始为显示
  controller: VideoController = new VideoController() // 视频控制器，用于控制视频播放

  build() {
    Column() {
      Video({
        src: this.videoSrc, // 视频源
        previewUri: this.previewUri, // 预览图
        currentProgressRate: this.curRate, // 播放速度
        controller: this.controller // 视频控制器
      })
        .width('100%')
        .height(600)
        .autoPlay(this.isAutoPlay) // 自动播放设置
        .controls(this.showControls) // 控制条显示设置
        .onStart(() => {
          console.info('onStart') // 视频开始播放事件
        })
        .onPause(() => {
          console.info('onPause') // 视频暂停事件
        })
        .onFinish(() => {
          console.info('onFinish') // 视频播放完成事件
        })
        .onError(() => {
          console.info('onError') // 视频播放错误事件
        })
        .onStop(() => {
          console.info('onStop') // 视频停止事件
        })
        .onPrepared((e?: DurationObject) => {
          if (e != undefined) {
            console.info('onPrepared is ' + e.duration) // 视频准备完成事件，输出视频时长
          }
        })
        .onSeeking((e?: TimeObject) => {
          if (e != undefined) {
            console.info('onSeeking is ' + e.time) // 视频跳转中事件，输出跳转时间
          }
        })
        .onSeeked((e?: TimeObject) => {
          if (e != undefined) {
            console.info('onSeeked is ' + e.time) // 视频跳转完成事件，输出跳转时间
          }
        })
        .onUpdate((e?: TimeObject) => {
          if (e != undefined) {
            console.info('onUpdate is ' + e.time) // 视频更新事件，输出当前播放时间
          }
        })

      Row() {
        Button('src').onClick(() => {
          this.videoSrc = $rawfile('video2.mp4') // 切换视频源为video2.mp4
        }).margin(5)
        Button('previewUri').onClick(() => {
          this.previewUri = $r('app.media.poster2') // 切换预览图为poster2
        }).margin(5)
        Button('controls').onClick(() => {
          this.showControls = !this.showControls // 切换控制条显示状态
        }).margin(5)
      }

      Row() {
        Button('start').onClick(() => {
          this.controller.start() // 开始播放
        }).margin(2)
        Button('pause').onClick(() => {
          this.controller.pause() // 暂停播放
        }).margin(2)
        Button('stop').onClick(() => {
          this.controller.stop() // 停止播放
        }).margin(2)
        Button('reset').onClick(() => {
          this.controller.reset() // 重置播放
        }).margin(2)
        Button('setTime').onClick(() => {
          this.controller.setCurrentTime(10, SeekMode.Accurate) // 设置播放时间为10秒
        }).margin(2)
      }

      Row() {
        Button('rate 0.75').onClick(() => {
          this.curRate = PlaybackSpeed.Speed_Forward_0_75_X // 设置播放速度为0.75倍
        }).margin(5)
        Button('rate 1').onClick(() => {
          this.curRate = PlaybackSpeed.Speed_Forward_1_00_X // 设置播放速度为1倍
        }).margin(5)
        Button('rate 2').onClick(() => {
          this.curRate = PlaybackSpeed.Speed_Forward_2_00_X // 设置播放速度为2倍
        }).margin(5)
      }
    }
  }
}

