@Component
struct Index {
  build() {
    Column({ space: 10 }) { // 创建一个垂直布局的容器，子组件之间的间距为10
      Chip({
        // 设置Chip的前缀图标
        prefixIcon: {
          src: $r('app.media.chips'), // 图标的资源路径
          size: { width: 16, height: 16 }, // 图标的尺寸
          fillColor: Color.Blue // 图标的填充颜色
        },
        // 设置Chip的标签文本
        label: {
          text: "操作块", // 标签文本内容
          fontSize: 12, // 文本字体大小
          fontColor: Color.Blue, // 文本颜色
          fontFamily: "HarmonyOS Sans", // 文本字体
          labelMargin: { left: 20, right: 30 } // 文本的左右边距
        },
        size: ChipSize.SMALL, // Chip的大小，这里设置为小尺寸
        allowClose: False, // 不允许显示删除图标
        enabled: True, // Chip是否启用
        backgroundColor: $r('sys.color.ohos_id_color_button_normal'), // Chip的背景颜色
        borderRadius: $r('sys.float.ohos_id_corner_radius_button'), // Chip的边框圆角
        onClose:()=>{
          console.log("chip on close") // 当Chip被关闭时的回调函数
        }
      })
    }
  }
}

import { Chip, ChipSize } from '@kit.ArkUI'; // 导入Chip组件及其相关属性

@Entry // 标记该组件为入口组件
@Component // 标记该结构体为ArkUI组件
struct Index {
  build() {
    Column({ space: 10 }) { // 创建一个垂直布局的容器，子组件之间的间距为10
      Chip({
        // 配置前缀图标
        prefixIcon: {
          src: $r('app.media.chips'), // 图标资源路径
          size: { width: 16, height: 16 }, // 图标大小
          fillColor: Color.Red // 图标填充颜色
        },
        // 配置标签文本
