import { Chip, ChipSize, LengthMetrics } from '@kit.ArkUI';

@Entry
@Component
struct ChipPage {
  @State message: string = 'Hello World'; // 定义一个状态变量message，初始值为'Hello World'

  build() {
    Column() {
      Chip({
        direction: Direction.Rtl, // 设置Chip组件的布局方向为从右到左
        prefixIcon: {
          src: $r('app.media.chips'), // 设置前缀图标的资源路径
          size: { width: 16, height: 16 }, // 设置前缀图标的大小
          fillColor: Color.Red, // 设置前缀图标的填充颜色
        },
        label: {
          text: "操作块", // 设置标签的文本内容
          fontSize: 12, // 设置标签的字体大小
          fontColor: Color.Blue, // 设置标签的字体颜色
          fontFamily: "HarmonyOS Sans", // 设置标签的字体家族
          localizedLabelMargin: { start: LengthMetrics.vp(20), end: LengthMetrics.vp(20) }, // 设置标签的边距
        },
        suffixIcon: {
          src: $r('app.media.close'), // 设置后缀图标的资源路径
          size: { width: 16, height: 16 }, // 设置后缀图标的大小
          fillColor: Color.Red, // 设置后缀图标的填充颜色
        },
        size: ChipSize.NORMAL, // 设置Chip组件的大小
        allowClose: False, // 设置是否允许关闭Chip组件
        enabled: True, // 设置Chip组件是否启用
        backgroundColor: $r('sys.color.ohos_id_color_button_normal'), // 设置Chip组件的背景颜色
        borderRadius: $r('sys.float.ohos_id_corner_radius_button') // 设置Chip组件的边框半径
      })
    }.justifyContent(FlexAlign.Center) // 设置Column组件的内容对齐方式为居中
    .width('100%') // 设置Column组件的宽度为100%
    .height('100%') // 设置Column组件的高度为100%
  }
}

import { Chip, ChipSize } from '@kit.ArkUI'; // 导入Chip组件及其相关属性

@Entry
@Component
struct Index {
  @State isActivated: boolean = False; // 定义一个状态变量，用于控制Chip的激活状态

  build() {
    Column({ space: 10 }) { // 创建一个垂直布局，组件间距为10
      Chip({
        prefixIcon: {
          src: $r('app.media.chips'), // 设置Chip的前缀图标资源路径
          size: { width: 16, height: 16 }, // 设置图标的大小
          fillColor: Color.Blue, // 设置图标的填充颜色
          activatedFillColor: $r('sys.color.ohos_id_color_text_primary_contrary') // 设置激活状态下的图标填充颜色
        },
        label: {
          text: "操作块", // 设置Chip的文本内容
          fontSize: 12, // 设置文本的字体大小
          fontColor: Color.Blue, // 设置文本的颜色
          activatedFontColor: $r('sys.color.ohos_id_color_text_primary_contrary'), // 设置激活状态下的文本颜色
          fontFamily: "HarmonyOS Sans", // 设置文本的字体家族
          labelMargin: { left: 20, right: 30 } // 设置文本的左右边距
        },
        size: ChipSize.NORMAL, // 设置Chip的大小为正常尺寸
        allowClose: True, // 允许Chip显示关闭按钮
        enabled: True, // 启用Chip的交互功能
        activated: this.isActivated, // 根据状态变量设置Chip的激活状态
        backgroundColor: $r('sys.color.ohos_id_color_button_normal'), // 设置Chip的背景颜色
        activatedBackgroundColor: $r('sys.color.ohos_id_color_emphasize'), // 设置激活状态下的背景颜色
        borderRadius: $r('sys.float.ohos_id_corner_radius_button'), // 设置Chip的边框圆角
        onClose:()=>{
          console.log("chip on close") // 定义Chip关闭事件的回调函数
        },
        onClicked:()=>{
          console.log("chip on clicked") // 定义Chip点击事件的回调函数
        }
      })

      Button('改变激活状态').onClick(()=>{
        this.isActivated = !this.isActivated; // 定义按钮点击事件的回调函数，切换Chip的激活状态
      })
    }
  }
}

import { Chip, ChipSize, SymbolGlyphModifier } from '@kit.ArkUI'; // 导入ArkUI库中的Chip组件及相关枚举和类

