import { ToolBar, ToolBarOptions } from '@kit.ArkUI'

// 定义工具栏项的状态枚举
enum ItemState {
  ENABLE = 1, // 启用状态
  DISABLE = 2, // 禁用状态
  ACTIVATE = 3 // 激活状态
}

@Entry
@Component
struct Index {
  @State toolbarList: ToolBarOptions = new ToolBarOptions() // 声明一个状态变量，用于存储工具栏项列表

  // 组件即将出现时执行的初始化操作
  aboutToAppear() {
    // 添加第一个工具栏项
    this.toolbarList.push({
      content: '剪贴我是超超超超超超超超超长样式', // 工具栏项的文本内容
      icon: $r('sys.media.ohos_ic_public_share'), // 工具栏项的图标资源
      action: () => {
        // 工具栏项的点击事件处理逻辑
      },
    })

    // 添加第二个工具栏项，并设置为禁用状态
    this.toolbarList.push({
      content: '拷贝', // 工具栏项的文本内容
      icon: $r('sys.media.ohos_ic_public_copy'), // 工具栏项的图标资源
      action: () => {
        // 工具栏项的点击事件处理逻辑
      },
      state: ItemState.DISABLE // 设置工具栏项的状态为禁用
    })

    // 添加第三个工具栏项，并设置为激活状态
    this.toolbarList.push({
      content: '粘贴', // 工具栏项的文本内容
      icon: $r('sys.media.ohos_ic_public_paste'), // 工具栏项的图标资源
      action: () => {
        // 工具栏项的点击事件处理逻辑
      },
      state: ItemState.ACTIVATE // 设置工具栏项的状态为激活
    })

    // 添加第四个工具栏项
    this.toolbarList.push({
      content: '全选', // 工具栏项的文本内容
      icon: $r('sys.media.ohos_ic_public_select_all'), // 工具栏项的图标资源
      action: () => {
        // 工具栏项的点击事件处理逻辑
      },
    })

    // 添加第五个工具栏项
    this.toolbarList.push({
      content: '分享', // 工具栏项的文本内容
      icon: $r('sys.media.ohos_ic_public_share'), // 工具栏项的图标资源
      action: () => {
        // 工具栏项的点击事件处理逻辑
      },
    })

    // 添加第六个工具栏项
    this.toolbarList.push({
      content: '分享', // 工具栏项的文本内容
      icon: $r('sys.media.ohos_ic_public_share'), // 工具栏项的图标资源
      action: () => {
        // 工具栏项的点击事件处理逻辑
      },
    })
  }

  build() {
    Row() {
      Stack() {
        Column() {
          // 创建ToolBar组件，并设置激活索引和工具栏项列表
          ToolBar({
            activateIndex: 2, // 设置激活的工具栏项索引
            toolBarList: this.toolbarList, // 传递工具栏项列表
          })
        }
      }.align(Alignment.Bottom) // 将Stack组件对齐到屏幕底部
      .width('100%').height('100%') // 设置Stack组件的宽度和高度为全屏
    }
  }
}

import { ComposeTitleBar, promptAction } from '@kit.ArkUI';

// 定义菜单项的接口，包含value（资源）、isEnabled（是否启用）和action（点击动作）
interface menuItem {
  value: Resource; // 菜单项的图标资源
  isEnabled?: boolean; // 菜单项是否启用，默认为true
  action?: () => void; // 点击菜单项时触发的动作
}

// 主入口组件
@Entry
@Component
struct Index {
  // 定义菜单项数组，包含四个菜单项，每个菜单项的图标相同，点击时显示相同的Toast消息
  private menuItems: Array<menuItem> = [
    {
      value: $r('app.media.ic_public_save'), // 菜单项图标
      isEnabled: true, // 菜单项启用
      action: () => promptAction.showToast({ message: "show toast index 1" }) // 点击时显示Toast消息
    },
    {
      value: $r('app.media.ic_public_save'),
      isEnabled: true,
      action: () => promptAction.showToast({ message: "show toast index 1" })
    },
    {
      value: $r('app.media.ic_public_save'),
      isEnabled: true,
      action: () => promptAction.showToast({ message: "show toast index 1" })
    },
    {
      value: $r('app.media.ic_public_save'),
      isEnabled: true,
      action: () => promptAction.showToast({ message: "show toast index 1" })
    },
  ];

  // 构建页面布局
  build() {
    Row() {
      Column() {
        // 第一个Divider，高度为2，颜色为0xCCCCCC
        Divider().height(2).color(0xCCCCCC)

        // 第一个ComposeTitleBar组件，显示标题、副标题和第一个菜单项
        ComposeTitleBar({
          title: "标题", // 主标题
          subtitle: "副标题", // 副标题
          menuItems: this.menuItems.slice(0, 1), // 只显示第一个菜单项
        })

        // 第二个Divider，高度为2，颜色为0xCCCCCC
        Divider().height(2).color(0xCCCCCC)

        // 第二个ComposeTitleBar组件，显示标题、副标题和前两个菜单项
        ComposeTitleBar({
          title: "标题",
          subtitle: "副标题",
          menuItems: this.menuItems.slice(0, 2), // 显示前两个菜单项
        })

        // 第三个Divider，高度为2，颜色为0xCCCCCC
        Divider().height(2).color(0xCCCCCC)

        // 第三个ComposeTitleBar组件，显示标题、副标题和所有菜单项
        ComposeTitleBar({
          title: "标题",
          subtitle: "副标题",
          menuItems: this.menuItems, // 显示所有菜单项
        })

        // 第四个Divider，高度为2，颜色为0xCCCCCC
        Divider().height(2).color(0xCCCCCC)

        // 第四个ComposeTitleBar组件，显示标题、副标题和一个自定义的菜单项
        ComposeTitleBar({
          menuItems: [{ 
            isEnabled: true, // 菜单项启用
            value: $r('app.media.ic_public_save'), // 菜单项图标
            action: () => promptAction.showToast({ message: "show toast index 1" }) // 点击时显示Toast消息
          }],
          title: "标题",
          subtitle: "副标题",
          item: { isEnabled: true, value: $r('app.media.app_icon') } // 自定义的菜单项
        })

        // 第五个Divider，高度为2，颜色为0xCCCCCC
        Divider().height(2).color(0xCCCCCC)
      }
      .height('100%') // 设置Column的高度为100%
    }
  }
}
