// 导入所需的模块和组件
import { IconType, ComposeListItem, promptAction } from '@kit.ArkUI';

// 定义一个名为 `ComposeListItemExample` 的组件，并将其标记为入口组件
@Entry
@Component
struct ComposeListItemExample {
  // 构建组件的 UI 结构
  build() {
    // 使用 `Column` 组件实现垂直布局
    Column() {
      // 使用 `List` 组件创建一个列表
      List() {
        // 使用 `ListItem` 组件创建一个列表项
        ListItem() {
          // 使用 `ComposeListItem` 组件创建一个包含左侧和右侧元素的列表项
          ComposeListItem({
            // 定义左侧的内容部分
            contentItem: ({
              // 设置图标的样式为普通图标
              iconStyle: IconType.NORMAL_ICON,
              // 设置图标为系统资源 `sys.media.ohos_app_icon`
              icon: $r('sys.media.ohos_app_icon'),
              // 设置主文本为“双行列表”
              primaryText: '双行列表',
              // 设置辅助文本为“辅助文字”
              secondaryText: '辅助文字',
              // 设置描述内容为“描述内容文字”
              description: '描述内容文字'
            }),
            // 定义右侧的操作部分
            operateItem: ({
              // 设置右侧图标为系统资源 `sys.media.ohos_app_icon`
              icon: {
                value: $r('sys.media.ohos_app_icon'),
                // 设置图标的点击事件，点击后弹出提示框显示“icon”
                action: () => {
                  promptAction.showToast({ message: 'icon' });
                }
              },
              // 设置右侧文本为“右侧文本”
              text: '右侧文本'
            })
          })
        }
      }
    }
  }
}
