import { ComposeTitleBar, promptAction } from '@kit.ArkUI'

// 定义菜单项的接口，包含值、启用状态和点击事件
interface menuItem {
  value: Resource; // 菜单项的图标资源
  isEnabled?: boolean; // 菜单项是否启用，默认为true
  action?: () => void; // 菜单项的点击事件
}

@Entry
@Component
struct Index {
  // 定义菜单项数组，包含四个菜单项，每个菜单项的图标相同，点击后显示相同的提示消息
  private menuItems: Array<menuItem> = [
    {
      value: $r('app.media.ic_public_save'), // 菜单项图标资源
      isEnabled: true, // 菜单项启用状态
      action: () => promptAction.showToast({ message: "show toast index 1" }) // 点击事件，显示提示消息
    },
    {
      value: $r('app.media.ic_public_save'),
      isEnabled: true,
      action: () => promptAction.showToast({ message: "show toast index 1" })
    },
    {
      value: $r('app.media.ic_public_save'),
      isEnabled: true,
      action: () => promptAction.showToast({ message: "show toast index 1" })
    },
    {
      value: $r('app.media.ic_public_save'),
      isEnabled: true,
      action: () => promptAction.showToast({ message: "show toast index 1" })
    },
  ]

  build() {
    Row() {
      Column() {
        Divider().height(2).color(0xCCCCCC) // 分隔线，高度为2vp，颜色为0xCCCCCC
        ComposeTitleBar({
          title: "标题", // 主标题
          subtitle: "副标题", // 副标题
          menuItems: this.menuItems.slice(0, 1), // 菜单项数组，取前一个菜单项
        })
        Divider().height(2).color(0xCCCCCC)
        ComposeTitleBar({
          title: "标题",
          subtitle: "副标题",
          menuItems: this.menuItems.slice(0, 2), // 菜单项数组，取前两个菜单项
        })
        Divider().height(2).color(0xCCCCCC)
        ComposeTitleBar({
          title: "标题",
          subtitle: "副标题",
          menuItems: this.menuItems, // 菜单项数组，取所有菜单项
        })
        Divider().height(2).color(0xCCCCCC)
        ComposeTitleBar({
          menuItems: [{ isEnabled: true, value: $r('app.media.ic_public_save'),
            action: () => promptAction.showToast({ message: "show toast index 1" })
          }], // 菜单项数组，包含一个菜单项
          title: "标题",
          subtitle: "副标题",
          item: { isEnabled: true, value: $r('app.media.app_icon') } // 额外的菜单项
        })
        Divider().height(2).color(0xCCCCCC)
      }
    }.height('100%') // 布局高度占据整个屏幕
  }
}
