import {
  FoldSplitContainer,
  PresetSplitRatio,
  ExtraRegionPosition,
  ExpandedRegionLayoutOptions,
  HoverModeRegionLayoutOptions,
  FoldedRegionLayoutOptions
} from '@kit.ArkUI';

@Component
struct Region {
  @Prop title: string; // 标题属性
  @BuilderParam content: () => void; // 内容构建器参数
  @Prop compBackgroundColor: string; // 组件背景颜色属性

  build() {
    Column({ space: 8 }) {
      Text(this.title)
        .fontSize("24fp") // 设置字体大小为24fp
        .fontWeight(600) // 设置字体粗细为600

      Scroll() {
        this.content() // 调用内容构建器
      }
      .layoutWeight(1) // 设置布局权重为1
      .width("100%") // 设置宽度为100%
    }
    .backgroundColor(this.compBackgroundColor) // 设置背景颜色
    .width("100%") // 设置宽度为100%
    .height("100%") // 设置高度为100%
    .padding(12) // 设置内边距为12
  }
}

const noop = () => {
};

@Component
struct SwitchOption {
  @Prop label: string = "" // 标签属性
  @Prop value: boolean = false // 值属性
  public onChange: (checked: boolean) => void = noop; // 改变事件处理函数

  build() {
    Row() {
      Text(this.label)
      Blank()
      Toggle({ type: ToggleType.Switch, isOn: this.value })
        .onChange((isOn) => {
          this.onChange(isOn); // 调用改变事件处理函数
        })
    }
    .backgroundColor(Color.White) // 设置背景颜色为白色
    .borderRadius(8) // 设置边框圆角为8
    .padding(8) // 设置内边距为8
    .width("100%") // 设置宽度为100%
  }
}

interface RadioOptions {
  label: string; // 标签属性
  value: Object | undefined | null; // 值属性
  onChecked: () => void; // 选中事件处理函数
}

@Component
struct RadioOption {
  @Prop label: string; // 标签属性
  @Prop value: Object | undefined | null; // 值属性
  @Prop options: Array<RadioOptions>; // 选项数组属性

  build() {
    Row() {
      Text(this.label)
      Blank()
      Column({ space: 4 }) {
        ForEach(this.options, (option: RadioOptions) => {
          Row() {
            Radio({
              group: this.label,
              value: JSON.stringify(option.value),
            })
              .checked(this.value === option.value) // 设置选中状态
              .onChange((checked) => {
                if (checked) {
                  option.onChecked(); // 调用选中事件处理函数
                }
              })
            Text(option.label)
          }
        })
      }
      .alignItems(HorizontalAlign.Start) // 设置对齐方式为左对齐
    }
    .alignItems(VerticalAlign.Top) // 设置对齐方式为顶部对齐
    .backgroundColor(Color.White) // 设置背景颜色为白色
    .borderRadius(8) // 设置边框圆角为8
    .padding(8) // 设置内边距为8
    .width("100%") // 设置宽度为100%
  }
}

@Entry
@Component
struct Index {
  @State expandedRegionLayoutOptions: ExpandedRegionLayoutOptions = {
    horizontalSplitRatio: PresetSplitRatio.LAYOUT_3V2, // 展开态水平宽度比
    verticalSplitRatio: PresetSplitRatio.LAYOUT_1V1, // 展开态垂直高度比
    isExtraRegionPerpendicular: true, // 展开态扩展区是否上下贯穿
    extraRegionPosition: ExtraRegionPosition.TOP // 展开态扩展区位置
  };
  @State foldingRegionLayoutOptions: HoverModeRegionLayoutOptions = {
    horizontalSplitRatio: PresetSplitRatio.LAYOUT_3V2, // 悬停态水平宽度比
    showExtraRegion: false, // 悬停态是否显示扩展区
    extraRegionPosition: ExtraRegionPosition.TOP // 悬停态扩展区位置
  };
  @State foldedRegionLayoutOptions: FoldedRegionLayoutOptions = {
    verticalSplitRatio: PresetSplitRatio.LAYOUT_1V1 // 折叠态垂直高度比
  };

  @Builder
  MajorRegion() {
    Region({
      title: "折叠态配置",
      compBackgroundColor: "rgba(255, 0, 0, 0.1)",
    }) {
      Column({ space: 4 }) {
        RadioOption({
          label: "折叠态垂直高度度比",
          value: this.foldedRegionLayoutOptions.verticalSplitRatio,
          options: [
            {
              label: "1:1",
              value: PresetSplitRatio.LAYOUT_1V1,
              onChecked: () => {
                this.foldedRegionLayoutOptions.verticalSplitRatio = PresetSplitRatio.LAYOUT_1V1
              }
            },
            {
              label: "2:3",
              value: PresetSplitRatio.LAYOUT_2V3,
              onChecked: () => {
                this.foldedRegionLayoutOptions.verticalSplitRatio = PresetSplitRatio.LAYOUT_2V3
              }
            },
            {
              label: "3:2",
              value: PresetSplitRatio.LAYOUT_3V2,
              onChecked: () => {
                this.foldedRegionLayoutOptions.verticalSplitRatio = PresetSplitRatio.LAYOUT_3V2
              }
            },
            {
              label: "未定义",
              value: undefined,
              onChecked: () => {
                this.foldedRegionLayoutOptions.verticalSplitRatio = undefined
              }
            }
          ]
        })
      }
      .constraintSize({ minHeight: "100%" }) // 设置最小高度为100%
    }
  }

  @Builder
  MinorRegion() {
    Region({
      title: "悬停态配置",
      compBackgroundColor: "rgba(0, 255, 0, 0.1)"
    }) {
      Column({ space: 4 }) {
        RadioOption({
          label: "悬停态水平宽度比",
          value: this.foldingRegionLayoutOptions.horizontalSplitRatio,
          options: [
            {
              label: "1:1",
              value: PresetSplitRatio.LAYOUT_1V1,
              onChecked: () => {
                this.foldingRegionLayoutOptions.horizontalSplitRatio = PresetSplitRatio.LAYOUT_1V1
              }
            },
            {
              label: "2:3",
              value: PresetSplitRatio.LAYOUT_2V3,
              onChecked: () => {
                this.foldingRegionLayoutOptions.horizontalSplitRatio = PresetSplitRatio.LAYOUT_2V3
              }
            },
            {
              label: "3:2",
              value: PresetSplitRatio.LAYOUT_3V2,
              onChecked: () => {
                this.foldingRegionLayoutOptions.horizontalSplitRatio = PresetSplitRatio.LAYOUT_3V2
              }
            },
            {
              label: "未定义",
              value: undefined,
              onChecked: () => {
                this.foldingRegionLayoutOptions.horizontalSplitRatio = undefined
              },
            ]
          })

          SwitchOption({
            label: "悬停态是否显示扩展区",
            value: this.foldingRegionLayoutOptions.showExtraRegion,
            onChange: (checked) => {
              this.foldingRegionLayoutOptions.showExtraRegion = checked;
            }
          })

          if (this.foldingRegionLayoutOptions.showExtraRegion) {
            RadioOption({
              label: "悬停态扩展区位置",
              value: this.foldingRegionLayoutOptions.extraRegionPosition,
              options: [
                {
                  label: "顶部",
                  value: ExtraRegionPosition.TOP,
                  onChecked: () => {
                    this.foldingRegionLayoutOptions.extraRegionPosition = ExtraRegionPosition.TOP
                  }
                },
                {
                  label: "底部",
                  value: ExtraRegionPosition.BOTTOM,
                  onChecked: () => {
                    this.foldingRegionLayoutOptions.extraRegionPosition = ExtraRegionPosition.BOTTOM
                  }
                },
                {
                  label: "未定义",
                  value: undefined,
                  onChecked: () => {
                    this.foldingRegionLayoutOptions.extraRegionPosition = undefined
                  }
                },
              ]
            })
          }
        }
        .constraintSize({ minHeight: "100%" }) // 设置最小高度为100%
      }
    }

    @Builder
    ExtraRegion() {
      Region({
        title: "展开态配置",
        compBackgroundColor: "rgba(0, 0, 255, 0.1)"
      }) {
        Column({ space: 4 }) {
          RadioOption({
            label: "展开态水平宽度比",
            value: this.expandedRegionLayoutOptions.horizontalSplitRatio,
            options: [
              {
                label: "1:1",
                value: PresetSplitRatio.LAYOUT_1V1,
                onChecked: () => {
                  this.expandedRegionLayoutOptions.horizontalSplitRatio = PresetSplitRatio.LAYOUT_1V1
                }
              },
              {
                label: "2:3",
                value: PresetSplitRatio.LAYOUT_2V3,
                onChecked: () => {
                  this.expandedRegionLayoutOptions.horizontalSplitRatio = PresetSplitRatio.LAYOUT_2V3
                }
              },
              {
                label: "3:2",
                value: PresetSplitRatio.LAYOUT_3V2,
                onChecked: () => {
                  this.expandedRegionLayoutOptions.horizontalSplitRatio = PresetSplitRatio.LAYOUT_3V2
                }
              },
              {
                label: "未定义",
                value: undefined,
                onChecked: () => {
                  this.expandedRegionLayoutOptions.horizontalSplitRatio = undefined
                }
              },
            ]
          })

          RadioOption({
            label: "展开态垂直高度度比",
            value: this.expandedRegionLayoutOptions.verticalSplitRatio,
            options: [
              {
                label: "1:1",
                value: PresetSplitRatio.LAYOUT_1V1,
                onChecked: () => {
                  this.expandedRegionLayoutOptions.verticalSplitRatio = PresetSplitRatio.LAYOUT_1V1
                }
              },
              {
                label: "2:3",
                value: PresetSplitRatio.LAYOUT_2V3,
                onChecked: () => {
                  this.expandedRegionLayoutOptions.verticalSplitRatio = PresetSplitRatio.LAYOUT_2V3
                }
              },
              {
                label: "3:2",
                value: PresetSplitRatio.LAYOUT_3V2,
                onChecked: () => {
                  this.expandedRegionLayoutOptions.verticalSplitRatio = PresetSplitRatio.LAYOUT_3V2
                }
              },
              {
                label: "未定义",
                value: undefined,
                onChecked: () => {
                  this.expandedRegionLayoutOptions.verticalSplitRatio = undefined
                }
              }
            ]
          })

          SwitchOption({
            label: "展开态扩展区是否上下贯穿",
            value: this.expandedRegionLayoutOptions.isExtraRegionPerpendicular,
            onChange: (checked) => {
              this.expandedRegionLayoutOptions.isExtraRegionPerpendicular = checked;
            }
          })

          if (!this.expandedRegionLayoutOptions.isExtraRegionPerpendicular) {
            RadioOption({
              label: "展开态扩展区位置",
              value: this.expandedRegionLayoutOptions.extraRegionPosition,
              options: [
                {
                  label: "顶部",
                  value: ExtraRegionPosition.TOP,
                  onChecked: () => {
                    this.expandedRegionLayoutOptions.extraRegionPosition = ExtraRegionPosition.TOP
                  }
                },
                {
                  label: "底部",
                  value: ExtraRegionPosition.BOTTOM,
                  onChecked: () => {
                    this.expandedRegionLayoutOptions.extraRegionPosition = ExtraRegionPosition.BOTTOM
                  }
                },
                {
                  label: "未定义",
                  value: undefined,
                  onChecked: () => {
                    this.expandedRegionLayoutOptions.extraRegionPosition = undefined
                  }
                },
              ]
            })
          }
        }
        .constraintSize({ minHeight: "100%" }) // 设置最小高度为100%
      }
    }

    build() {
      Column() {
        FoldSplitContainer({
          primary: () => {
            this.MajorRegion() // 调用MajorRegion区域的内容
          },
          secondary: () => {
            this.MinorRegion() // 调用MinorRegion区域的内容
          },
          extra: () => {
            this.ExtraRegion() // 调用ExtraRegion区域的内容
          },
          expandedLayoutOptions: this.expandedRegionLayoutOptions, // 展开态布局选项
          hoverModeLayoutOptions: this.foldingRegionLayoutOptions, // 悬停态布局选项
          foldedLayoutOptions: this.foldedRegionLayoutOptions, // 折叠态布局选项
        })
      }
      .width("100%") // 设置宽度为100%
      .height("100%") // 设置高度为100%
    }
  }
}
