// 导入ToolBar和ToolBarOptions组件
import { ToolBar, ToolBarOptions } from '@kit.ArkUI'

// 定义按钮状态的枚举
enum ItemState {
  ENABLE = 1, // 启用状态
  DISABLE = 2, // 禁用状态
  ACTIVATE = 3 // 激活状态
}

// 主入口组件
@Entry
@Component
struct Index {
  // 定义工具栏选项列表的状态变量
  @State toolbarList: ToolBarOptions = new ToolBarOptions()

  // 组件即将显示时的生命周期方法
  aboutToAppear() {
    // 添加第一个工具栏按钮
    this.toolbarList.push({
      content: '剪贴我是超超超超超超超超超长样式', // 按钮文本内容
      icon: $r('sys.media.ohos_ic_public_share'), // 按钮图标资源
      action: () => { // 按钮点击事件
        // 点击事件处理逻辑
      },
    })

    // 添加第二个工具栏按钮，设置为禁用状态
    this.toolbarList.push({
      content: '拷贝', // 按钮文本内容
      icon: $r('sys.media.ohos_ic_public_copy'), // 按钮图标资源
      action: () => { // 按钮点击事件
        // 点击事件处理逻辑
      },
      state: ItemState.DISABLE // 按钮状态为禁用
    })

    // 添加第三个工具栏按钮，设置为激活状态
    this.toolbarList.push({
      content: '粘贴', // 按钮文本内容
      icon: $r('sys.media.ohos_ic_public_paste'), // 按钮图标资源
      action: () => { // 按钮点击事件
        // 点击事件处理逻辑
      },
      state: ItemState.ACTIVATE // 按钮状态为激活
    })

    // 添加第四个工具栏按钮
    this.toolbarList.push({
      content: '全选', // 按钮文本内容
      icon: $r('sys.media.ohos_ic_public_select_all'), // 按钮图标资源
      action: () => { // 按钮点击事件
        // 点击事件处理逻辑
      },
    })

    // 添加第五个工具栏按钮
    this.toolbarList.push({
      content: '分享', // 按钮文本内容
      icon: $r('sys.media.ohos_ic_public_share'), // 按钮图标资源
      action: () => { // 按钮点击事件
        // 点击事件处理逻辑
      },
    })

    // 添加第六个工具栏按钮
    this.toolbarList.push({
      content: '分享', // 按钮文本内容
      icon: $r('sys.media.ohos_ic_public_share'), // 按钮图标资源
      action: () => { // 按钮点击事件
        // 点击事件处理逻辑
      },
    })
  }

  // 构建组件的UI结构
  build() {
    Row() {
      Stack() {
        Column() {
          // 创建工具栏组件
          ToolBar({
            activateIndex: 2, // 设置激活按钮的索引
            toolBarList: this.toolbarList, // 工具栏按钮列表
          })
        }
      }
      .align(Alignment.Bottom) // 设置Stack组件对齐方式为底部对齐
      .width('100%') // 设置Stack组件宽度为100%
      .height('100%') // 设置Stack组件高度为100%
    }
  }
}
