@Entry
@Component
struct MenuExample {
  @State selected: number = 0; // 用于记录当前选中的菜单项索引

  build() {
    Flex({ direction: FlexDirection.Column, alignItems: ItemAlign.Start, justifyContent: FlexAlign.SpaceBetween }) {
      Text('Menu').fontSize(20).fontColor(Color.Black).margin({ top: 10, bottom: 10 }) // 标题文本，字体大小为20，颜色为黑色，上下边距为10vp

      Menu() {
        MenuItemGroup({ header: 'Group1' }) { // 菜单项组1，标题为"Group1"
          MenuItem({ content: 'Item1' }) // 菜单项1，内容为"Item1"
          MenuItem({ content: 'Item2' }) // 菜单项2，内容为"Item2"
        }
        MenuItemGroup({ header: 'Group2' }) { // 菜单项组2，标题为"Group2"
          MenuItem({ content: 'Item3' }) // 菜单项3，内容为"Item3"
          MenuItem({ content: 'Item4' }) // 菜单项4，内容为"Item4"
        }
      }
      .width('100%') // 菜单宽度占据父容器的100%
      .height(200) // 菜单高度为200vp
      .backgroundColor(Color.White) // 菜单背景颜色为白色
      .borderRadius(10) // 菜单边框圆角为10vp
      .padding(10) // 菜单内边距为10vp
      .onSelect((value: number) => { // 菜单项选中事件
        this.selected = value; // 更新选中状态
        console.log(`Selected: ${value}`); // 打印选中项的索引
      })

      Text(`Selected: ${this.selected}`).fontSize(16).fontColor(Color.Gray).margin({ top: 20 }) // 显示当前选中的菜单项索引，字体大小为16，颜色为灰色，上边距为20vp
    }
    .width('100%') // 整体布局宽度占据父容器的100%
    .height(400) // 整体布局高度为400vp
    .padding({ left: 35, right: 35, top: 35 }) // 整体布局内边距为左右35vp、顶部35vp
    .backgroundColor(Color.LightGray) // 整体布局背景颜色为浅灰色
  }
}
