@Entry
@Component
struct MenuExample {
  build() {
    Column() {
      // 创建一个按钮，点击按钮后会弹出上下文菜单
      Button('Open Menu')
        .width(100) // 按钮宽度为 100vp
        .height(50) // 按钮高度为 50vp
        .padding(10) // 按钮内边距为 10vp
        .backgroundColor('#007DFF') // 按钮背景颜色为 #007DFF
        .fontColor(Color.White) // 按钮字体颜色为白色
        .fontSize(16) // 按钮字体大小为 16fp
        .onClick(() => {
          // 点击按钮后调用 ContextMenu 方法显示菜单
          ContextMenu.show({
            start: { x: 100, y: 100 }, // 菜单显示的起始位置
            items: [
              // 创建第一个菜单项
              MenuItem({
                content: 'Item 1', // 菜单项内容为 "Item 1"
                action: () => {
                  console.log('Item 1 clicked'); // 点击菜单项后打印日志
                }
              }),
              // 创建第二个菜单项
              MenuItem({
                content: 'Item 2', // 菜单项内容为 "Item 2"
                action: () => {
                  console.log('Item 2 clicked'); // 点击菜单项后打印日志
                }
              })
            ]
          });
        })
    }
    .width('100%') // 布局宽度占据整个屏幕
    .height('100%') // 布局高度占据整个屏幕
    .padding(10) // 布局内边距为 10vp
    .backgroundColor('#FFFFFF') // 布局背景颜色为白色
  }
}
