@Entry
@Component
struct StepperTabsExample {
  @State value: number = 0; // 步进器的初始值

  build() {
    Flex({ direction: FlexDirection.Column, alignItems: ItemAlign.Center, justifyContent: FlexAlign.Center }) {
      // Stepper组件
      Flex({ alignItems: ItemAlign.Center, justifyContent: FlexAlign.Center }) {
        Stepper({ min: 0, max: 10, step: 1, value: this.value }) // 步进器的最小值、最大值、步长和初始值
          .width('100%') // 宽度为100%
          .height(50) // 高度为50vp
          .backgroundColor('#007DFF') // 背景颜色为蓝色
          .fontColor('#FFFFFF') // 文本颜色为白色
          .buttonWidth(40) // 按钮宽度为40vp
          .buttonHeight(40) // 按钮高度为40vp
          .onChange((value: number) => {
            this.value = value; // 当步进器的值改变时，更新状态
          })
      }
      .width('100%')
      .height(50)

      // Tabs组件
      Flex({ alignItems: ItemAlign.Center, justifyContent: FlexAlign.Center }) {
        Tabs() {
          TabContent() {
            Text('Tab 1') // 第一个选项卡的内容
              .fontSize(20)
              .fontColor('#000000')
          }
          .tabBar({ icon: 'icon1.png', text: 'Tab 1' }) // 第一个选项卡的标题

          TabContent() {
            Text('Tab 2') // 第二个选项卡的内容
              .fontSize(20)
              .fontColor('#000000')
          }
          .tabBar({ icon: 'icon2.png', text: 'Tab 2' }) // 第二个选项卡的标题

          TabContent() {
            Text('Tab 3') // 第三个选项卡的内容
              .fontSize(20)
              .fontColor('#000000')
          }
          .tabBar({ icon: 'icon3.png', text: 'Tab 3' }) // 第三个选项卡的标题
        }
        .width('100%') // 宽度为100%
        .height(300) // 高度为300vp
        .backgroundColor('#F0F0F0') // 背景颜色为浅灰色
        .barWidth(100) // 选项卡标题的宽度为100vp
        .barHeight(50) // 选项卡标题的高度为50vp
        .barMode(BarMode.Fixed) // 选项卡标题的布局模式为固定
        .barSelectedColor('#007DFF') // 选中选项卡标题的颜色为蓝色
        .barBackgroundColor('#FFFFFF') // 选项卡标题的背景颜色为白色
      }
      .width('100%')
      .height(300)
    }
    .width('100%')
    .height('100%')
  }
}
