// 定义一个类bottom，用于存储底部间距的值
class bottom {
  bottom: number = 50 // 底部间距为50
}

// 创建bottom类的实例，用于设置TextPicker组件的margin属性
let bott: bottom = new bottom()

// 主入口组件，使用@Entry和@Component装饰器标记
@Entry
@Component
struct TextPickerExample {
  // 定义一个私有变量select，用于存储单列选择器的初始选中项索引
  private select: number = 1

  // 定义三个私有数组，分别存储不同种类的水果
  private apfruits: string[] = ['apple1', 'apple2', 'apple3', 'apple4']
  private orfruits: string[] = ['orange1', 'orange2', 'orange3', 'orange4']
  private pefruits: string[] = ['peach1', 'peach2', 'peach3', 'peach4']

  // 定义一个二维数组multi，用于存储多列选择器的选项
  private multi: string[][] = [this.apfruits, this.orfruits, this.pefruits]

  // 定义一个私有数组cascade，用于存储多列联动选择器的选项
  private cascade: TextCascadePickerRangeContent[] = [
    {
      text: '辽宁省',
      children: [
        { text: '沈阳市', children: [{ text: '沈河区' }, { text: '和平区' }, { text: '浑南区' }] },
        { text: '大连市', children: [{ text: '中山区' }, { text: '金州区' }, { text: '长海县' }] }
      ]
    },
    {
      text: '吉林省',
      children: [
        { text: '长春市', children: [{ text: '南关区' }, { text: '宽城区' }, { text: '朝阳区' }] },
        { text: '四平市', children: [{ text: '铁西区' }, { text: '铁东区' }, { text: '梨树县' }] }
      ]
    },
    {
      text: '黑龙江省',
      children: [
        { text: '哈尔滨市', children: [{ text: '道里区' }, { text: '道外区' }, { text: '南岗区' }] },
        { text: '牡丹江市', children: [{ text: '东安区' }, { text: '西安区' }, { text: '爱民区' }] }
      ]
    }
  ]

  // 构建组件的UI结构
  build() {
    Column() {
      // 单列选择器，范围为apfruits数组，初始选中项索引为select
      TextPicker({ range: this.apfruits, selected: this.select })
        .onChange((value: string | string[], index: number | number[]) => {
          // 当选择项改变时，输出当前选中的值和索引
          console.info('Picker item changed, value: ' + value + ', index: ' + index)
        }).margin(bott) // 设置底部间距

      // 多列选择器，范围为multi数组
      TextPicker({ range: this.multi })
        .onChange((value: string | string[], index: number | number[]) => {
          // 当选择项改变时，输出当前选中的值和索引
          console.info('TextPicker 多列:onChange ' + JSON.stringify(value) + ', ' + 'index: ' + JSON.stringify(index))
        }).margin(bott) // 设置底部间距

      // 多列联动选择器，范围为cascade数组
      TextPicker({ range: this.cascade })
        .onChange((value: string | string[], index: number | number[]) => {
          // 当选择项改变时，输出当前选中的值和索引
          console.info('TextPicker 多列联动:onChange ' + JSON.stringify(value) + ', ' + 'index: ' + JSON.stringify(index))
        })
    }
  }
}
