// xxx.ets
@Entry
@Component
struct ColumnExample {
  build() {
    Column({ space: 5 }) { // 设置子元素垂直方向间距为5
      Text('space').width('90%') // 显示标题 "space"，宽度为90%

      Column({ space: 5 }) { // 设置子元素垂直方向间距为5
        Column().width('100%').height(30).backgroundColor(0xAFEEEE) // 子元素1，宽度100%，高度30vp，背景颜色为浅蓝色
        Column().width('100%').height(30).backgroundColor(0x00FFFF) // 子元素2，宽度100%，高度30vp，背景颜色为青色
      }.width('90%').height(100).border({ width: 1 }) // 子元素组，宽度90%，高度100vp，边框宽度为1

      Text('alignItems(Start)').width('90%') // 显示标题 "alignItems(Start)"，宽度为90%
      Column() {
        Column().width('50%').height(30).backgroundColor(0xAFEEEE) // 子元素1，宽度50%，高度30vp，背景颜色为浅蓝色
        Column().width('50%').height(30).backgroundColor(0x00FFFF) // 子元素2，宽度50%，高度30vp，背景颜色为青色
      }.alignItems(HorizontalAlign.Start).width('90%').border({ width: 1 }) // 子元素组，宽度90%，水平对齐方式为Start，边框宽度为1

      Text('alignItems(End)').width('90%') // 显示标题 "alignItems(End)"，宽度为90%
      Column() {
        Column().width('50%').height(30).backgroundColor(0xAFEEEE) // 子元素1，宽度50%，高度30vp，背景颜色为浅蓝色
        Column().width('50%').height(30).backgroundColor(0x00FFFF) // 子元素2，宽度50%，高度30vp，背景颜色为青色
      }.alignItems(HorizontalAlign.End).width('90%').border({ width: 1 }) // 子元素组，宽度90%，水平对齐方式为End，边框宽度为1

      Text('alignItems(Center)').width('90%') // 显示标题 "alignItems(Center)"，宽度为90%
      Column() {
        Column().width('50%').height(30).backgroundColor(0xAFEEEE) // 子元素1，宽度50%，高度30vp，背景颜色为浅蓝色
        Column().width('50%').height(30).backgroundColor(0x00FFFF) // 子元素2，宽度50%，高度30vp，背景颜色为青色
      }.alignItems(HorizontalAlign.Center).width('90%').border({ width: 1 }) // 子元素组，宽度90%，水平对齐方式为Center，边框宽度为1

      Text('justifyContent(Center)').width('90%') // 显示标题 "justifyContent(Center)"，宽度为90%
      Column() {
        Column().width('90%').height(30).backgroundColor(0xAFEEEE) // 子元素1，宽度90%，高度30vp，背景颜色为浅蓝色
        Column().width('90%').height(30).backgroundColor(0x00FFFF) // 子元素2，宽度90%，高度30vp，背景颜色为青色
      }.height(100).border({ width: 1 }).justifyContent(FlexAlign.Center) // 子元素组，高度100vp，边框宽度为1，垂直对齐方式为Center

      Text('justifyContent(End)').width('90%') // 显示标题 "justifyContent(End)"，宽度为90%
      Column() {
        Column().width('90%').height(30).backgroundColor(0xAFEEEE) // 子元素1，宽度90%，高度30vp，背景颜色为浅蓝色
        Column().width('90%').height(30).backgroundColor(0x00FFFF) // 子元素2，宽度90%，高度30vp，背景颜色为青色
      }.height(100).border({ width: 1 }).justifyContent(FlexAlign.End) // 子元素组，高度100vp，边框宽度为1，垂直对齐方式为End
    }.width('100%').padding({ top: 5 }) // 主Column，宽度100%，顶部内边距为5vp
  }
}
