@Entry
@Component
struct HyperlinkExample {
  build() {
    Column() {
      Column() {
        Hyperlink('https://example.com/') {
          Image($r('app.media.bg')) // 显示名为 'app.media.bg' 的图片资源
            .width(200) // 设置图片宽度为 200vp
            .height(100) // 设置图片高度为 100vp
        }
      }

      Column() {
        Hyperlink('https://example.com/', 'Go to the developer website') {
        }
        .color(Color.Blue) // 设置 Hyperlink 文本颜色为蓝色
      }
    }.width('100%').height('100%').justifyContent(FlexAlign.Center) // 设置布局宽度为 100%，高度为 100%，内容在垂直方向上居中对齐
  }
}
