@Entry
@Component
struct ButtonExample {
  build() {
    Flex({ direction: FlexDirection.Column, alignItems: ItemAlign.Center, justifyContent: FlexAlign.Center }) {
      // 标题部分
      Text('按钮示例')
        .fontSize(24)
        .fontWeight(FontWeight.Bold)
        .margin({ bottom: 20 })
        .textAlign(TextAlign.Center)

      // 普通按钮部分
      Flex({ direction: FlexDirection.Row, alignItems: ItemAlign.Center, justifyContent: FlexAlign.SpaceAround }) {
        Button('普通按钮')
          .width(150)
          .height(50)
          .backgroundColor('#007DFF')
          .borderRadius(8)
          .onClick(() => {
            // 点击事件处理逻辑
            console.log('普通按钮被点击');
          })

        Button('禁用按钮')
          .width(150)
          .height(50)
          .backgroundColor('#CCCCCC')
          .borderRadius(8)
          .enabled(false)
          .onClick(() => {
            // 点击事件处理逻辑
            console.log('禁用按钮被点击');
          })
      }
      .margin({ bottom: 20 })

      // 胶囊按钮部分
      Flex({ direction: FlexDirection.Row, alignItems: ItemAlign.Center, justifyContent: FlexAlign.SpaceAround }) {
        Button('胶囊按钮')
          .width(150)
          .height(50)
          .backgroundColor('#007DFF')
          .borderRadius(25)
          .onClick(() => {
            // 点击事件处理逻辑
            console.log('胶囊按钮被点击');
          })

        Button('禁用胶囊按钮')
          .width(150)
          .height(50)
          .backgroundColor('#CCCCCC')
          .borderRadius(25)
          .enabled(false)
          .onClick(() => {
            // 点击事件处理逻辑
            console.log('禁用胶囊按钮被点击');
          })
      }
      .margin({ bottom: 20 })

      // 圆形按钮部分
      Flex({ direction: FlexDirection.Row, alignItems: ItemAlign.Center, justifyContent: FlexAlign.SpaceAround }) {
        Button('圆形按钮')
          .width(80)
          .height(80)
          .backgroundColor('#007DFF')
          .borderRadius(40)
          .onClick(() => {
            // 点击事件处理逻辑
            console.log('圆形按钮被点击');
          })

        Button('禁用圆形按钮')
          .width(80)
          .height(80)
          .backgroundColor('#CCCCCC')
          .borderRadius(40)
          .enabled(false)
          .onClick(() => {
            // 点击事件处理逻辑
            console.log('禁用圆形按钮被点击');
          })
      }
    }
    .width('100%')
    .height(400)
    .padding({ left: 35, right: 35, top: 35 })
    .backgroundColor('#F1F3F5')
  }
}
