// xxx.ets
@Entry
@Component
struct EllipseExample {
  build() {
    Column({ space: 10 }) { // 创建一个垂直布局，子组件之间的间距为 10vp
      // 绘制一个 150 * 80 的椭圆
      Ellipse({ width: 150, height: 80 }) // 创建一个宽度为 150vp，高度为 80vp 的椭圆，填充颜色为默认颜色

      // 绘制一个 150 * 100 、线条为蓝色的椭圆环
      Ellipse()
        .width(150) // 设置椭圆的宽度为 150vp
        .height(100) // 设置椭圆的高度为 100vp
        .fillOpacity(0) // 设置椭圆的填充透明度为 0，即无填充颜色
        .stroke(Color.Blue) // 设置椭圆的边框颜色为蓝色
        .strokeWidth(3) // 设置椭圆的边框宽度为 3vp
    }.width('100%') // 设置 Column 的宽度为 100%，占据整个屏幕宽度
  }
}
