@Entry
@Component
struct RectExample {
  build() {
    Column({ space: 10 }) { // 创建一个垂直布局的容器，子元素之间的间距为10vp
      Column()
        .width(100) // 设置Column组件的宽度为100vp
        .height(100) // 设置Column组件的高度为100vp
        .linearGradient({ // 设置Column组件的背景为线性渐变
          direction: GradientDirection.Right, // 渐变方向为从左到右
          colors: [[0xff0000, 0.0], [0x0000ff, 0.3], [0xffff00, 1.0]] // 渐变颜色及位置，从红色到蓝色再到黄色
        })
        .clip(new Rect({ width: 100, height: 100, radius: 40 })) // 裁剪Column组件为圆角矩形，圆角半径为40vp

      Rect() // 创建一个Rect组件
        .width(100) // 设置Rect组件的宽度为100vp
        .height(100) // 设置Rect组件的高度为100vp
        .fill(Color.Pink) // 设置Rect组件的背景颜色为粉色
        .radius(40) // 设置Rect组件的边角半径为40vp
        .stroke(Color.Black) // 设置Rect组件的边框颜色为黑色
        .linearGradient({ // 设置Rect组件的背景为线性渐变
          direction: GradientDirection.Right, // 渐变方向为从左到右
          colors: [[0xff0000, 0.0], [0x0000ff, 0.3], [0xffff00, 1.0]] // 渐变颜色及位置，从红色到蓝色再到黄色
        })
    }
  }
}
