// xxx.ets
import { photoAccessHelper } from '@kit.MediaLibraryKit'; // 导入媒体库助手模块
import { fileIo } from '@kit.CoreFileKit'; // 导入文件IO模块

@Entry
@Component
struct Index {
  build() {
    Row() {
      Column({space:10}) { // 创建一个Column组件，设置组件之间的间距为10
        // 默认参数下，图标、文字、背景都存在
        SaveButton().onClick(async (event:ClickEvent, result:SaveButtonOnClickResult) => { // 创建一个SaveButton组件，并设置点击事件
          if (result == SaveButtonOnClickResult.SUCCESS) { // 如果点击结果为成功
            try {
              const context = getContext(this); // 获取当前组件的上下文
              let helper = photoAccessHelper.getPhotoAccessHelper(context); // 获取媒体库助手实例
              // onClick触发后10秒内通过createAsset接口创建图片文件，10秒后createAsset权限收回。
              let uri = await helper.createAsset(photoAccessHelper.PhotoType.IMAGE, 'png'); // 创建一个图片文件，返回文件的URI
              // 使用uri打开文件，可以持续写入内容，写入过程不受时间限制
              let file = await fileIo.open(uri, fileIo.OpenMode.READ_WRITE | fileIo.OpenMode.CREATE); // 打开文件，设置为可读写模式
              // 写入文件
              await fileIo.write(file.fd, "context"); // 向文件中写入内容
              // 关闭文件
              await fileIo.close(file.fd); // 关闭文件
            } catch (error) {
              console.error("error is " + JSON.stringify(error)); // 捕获并打印错误信息
            }
          }
        })
        // 传入参数即表示元素存在，不传入的参数表示元素不存在，如果不传入buttonType，会默认添加ButtonType.Capsule配置，显示图标+背景。
        SaveButton({icon:SaveIconStyle.FULL_FILLED}) // 创建一个SaveButton组件，设置图标样式为FULL_FILLED
        // 只显示图标+背景，如果设置背景色高八位的α值低于0x1A，则会被系统强制调整为0xFF
        SaveButton({icon:SaveIconStyle.FULL_FILLED, buttonType:ButtonType.Capsule}) // 创建一个SaveButton组件，设置图标样式为FULL_FILLED，按钮类型为Capsule
          .backgroundColor(0x10007dff) // 设置按钮的背景颜色
        // 图标、文字、背景都存在，如果设置背景色高八位的α值低于0x1A，则会被系统强制调整为0xFF
        SaveButton({icon:SaveIconStyle.FULL_FILLED, text:SaveDescription.DOWNLOAD, buttonType:ButtonType.Capsule}) // 创建一个SaveButton组件，设置图标样式为FULL_FILLED，文字描述为DOWNLOAD，按钮类型为Capsule
      }.width('100%') // 设置Column的宽度为100%
    }.height('100%') // 设置Row的高度为100%
  }
}
