private isShowText: boolean = true;
@State isToggleOn: boolean = true;
build() {
Row() {
Text('Switch')
Toggle({ type: ToggleType.Switch, isOn: this.isToggleOn })
.selectedColor($r('app.color.purple_700')) // 设置Switch打开状态的背景颜色
.switchPointColor($r('app.color.purple_200')) // 设置Switch类型的圆形滑块颜色
.width(56) // 设置组件自身的宽度
.height(30) // 设置组件自身的高度
.margin(8)
.visibility(Visibility.Visible)
.backgroundColor(Color.Transparent)
.onChange((isOn: boolean) => {
this.isToggleOn = isOn;
})
if (this.isShowText) {
Text(this.isToggleOn? '开': '关')
}
}
}

